import React, { useState, useEffect, useCallback, useMemo } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import FormSection from './FormSection';
import ProgressBar from './ProgressBar';
import VehicleManager from './VehicleManager';
import { calculateProgress, validateForm } from '../utils/formUtils';
import { submitCarbonData } from '../services/api';

const Container = styled.div`
  max-width: 1600px;
  margin: 0 auto;
  padding: 30px;
  background: rgba(255, 255, 255, 0.98);
  min-height: 100vh;
  backdrop-filter: blur(20px);
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.15);
  border-radius: 30px;
  margin: 20px;
  overflow: hidden;
  border: 2px solid rgba(255, 255, 255, 0.3);
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #4CAF50, #45a049, #2E8B57);
    border-radius: 30px 30px 0 0;
  }
  
  @media (max-width: 768px) {
    margin: 10px;
    padding: 20px;
    border-radius: 20px;
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 40px;
  padding: 30px 0;
  background: linear-gradient(135deg, rgba(76, 175, 80, 0.05), rgba(69, 160, 73, 0.05));
  border-radius: 20px;
  border: 1px solid rgba(76, 175, 80, 0.1);
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(76, 175, 80, 0.03) 0%, transparent 70%);
    animation: rotate 20s linear infinite;
  }
  
  @keyframes rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }
`;

const Title = styled.h1`
  font-size: 3em;
  margin-bottom: 20px;
  background: linear-gradient(135deg, #2E8B57, #3CB371, #4CAF50);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 0 4px 8px rgba(46, 139, 87, 0.3);
  font-weight: 700;
  letter-spacing: -0.5px;
  position: relative;
  z-index: 1;
  
  @media (max-width: 768px) {
    font-size: 2.2em;
  }
`;

const Subtitle = styled.p`
  font-size: 1.3em;
  color: #555;
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.7;
  font-weight: 400;
  position: relative;
  z-index: 1;
  
  @media (max-width: 768px) {
    font-size: 1.1em;
  }
`;

const InfoSection = styled.div`
  background: linear-gradient(135deg, #E8F5E8, #F0F8F0);
  border-left: 5px solid #2E8B57;
  margin-bottom: 25px;
  padding: 25px;
  border-radius: 18px;
  box-shadow: 0 6px 25px rgba(0,0,0,0.08);
`;

const InfoContent = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
  margin-bottom: 15px;
`;

const InfoIcon = styled.div`
  font-size: 2em;
`;

const InfoText = styled.div`
  h3 {
    margin: 0 0 8px 0;
    color: #2E8B57;
    font-size: 1.3em;
  }
  p {
    margin: 0;
    color: #555;
    line-height: 1.5;
  }
`;

const InfoLink = styled.div`
  text-align: center;
  
  a {
    color: #2E8B57;
    text-decoration: none;
    font-weight: 600;
    padding: 10px 20px;
    background: rgba(46, 139, 87, 0.1);
    border-radius: 20px;
    display: inline-block;
    transition: all 0.3s ease;
    
    &:hover {
      background: rgba(46, 139, 87, 0.2);
      transform: translateY(-2px);
    }
  }
`;

const FormGrid = styled.div`
  display: flex;
  justify-content: center;
  margin: 20px 0;
  padding: 0 15px;
  height: 500px;
  overflow: hidden;
  
  @media (max-width: 1000px) {
    padding: 0 8px;
    height: 450px;
  }
  
  @media (max-width: 768px) {
    margin: 15px 0;
    height: 400px;
  }
`;

const StepNavigation = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 30px 0;
  padding: 0 20px;
  
  @media (max-width: 768px) {
    margin: 20px 0;
    padding: 0 10px;
  }
`;

const StepButton = styled.button`
  background: linear-gradient(135deg, #4CAF50, #45a049);
  color: white;
  padding: 12px 25px;
  border: none;
  border-radius: 20px;
  cursor: pointer;
  font-size: 14px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  transition: all 0.3s ease;
  box-shadow: 0 3px 10px rgba(76, 175, 80, 0.3);
  position: relative;
  overflow: hidden;
  min-width: 120px;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(76, 175, 80, 0.4);
    background: linear-gradient(135deg, #45a049, #4CAF50);
  }
  
  &:disabled {
    opacity: 0.4;
    cursor: not-allowed;
    transform: none;
    background: #ccc;
  }
  
  &.prev-button {
    background: linear-gradient(135deg, #6c757d, #5a6268);
    box-shadow: 0 3px 10px rgba(108, 117, 125, 0.3);
    
    &:hover:not(:disabled) {
      background: linear-gradient(135deg, #5a6268, #6c757d);
      box-shadow: 0 6px 20px rgba(108, 117, 125, 0.4);
    }
  }
  
  &.reset-button {
    background: linear-gradient(135deg, #f44336, #d32f2f);
    box-shadow: 0 3px 10px rgba(244, 67, 54, 0.3);
    
    &:hover {
      background: linear-gradient(135deg, #d32f2f, #f44336);
      box-shadow: 0 6px 20px rgba(244, 67, 54, 0.4);
    }
  }
`;

const StepIndicator = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  font-weight: 600;
  color: #2c3e50;
  font-size: 16px;
  
  @media (max-width: 768px) {
    font-size: 14px;
  }
`;

const StepDots = styled.div`
  display: flex;
  gap: 8px;
  align-items: center;
`;

const StepDot = styled.div`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: ${props => props.active ? '#4CAF50' : '#e9ecef'};
  transition: all 0.3s ease;
  cursor: pointer;
  
  &:hover {
    background: ${props => props.active ? '#45a049' : '#dee2e6'};
    transform: scale(1.2);
  }
`;

const SubmitButton = styled.div`
  text-align: center;
  margin-top: 30px;
  
  button {
    background: linear-gradient(135deg, #4CAF50, #45a049);
    color: white;
    padding: 15px 30px;
    border: none;
    border-radius: 25px;
    cursor: pointer;
    font-size: 16px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.8px;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(76, 175, 80, 0.3);
    position: relative;
    overflow: hidden;
    max-width: 400px;
    width: 100%;
    
    &:hover {
      transform: translateY(-3px);
      box-shadow: 0 8px 25px rgba(76, 175, 80, 0.4);
      background: linear-gradient(135deg, #45a049, #4CAF50);
    }
    
    &:disabled {
      opacity: 0.6;
      cursor: not-allowed;
      transform: none;
    }
  }
`;

const CarbonCalculator = () => {
  const navigate = useNavigate();
  
  // Define default form data as a memoized constant
  const defaultFormData = useMemo(() => ({
    household_size: 4,
    home_size_sqft: 1200,
    home_type: 'apartment',
    home_age: 10,
    electricity_usage_kwh: 300,
    heating_energy_source: 'electric',
    cooling_energy_source: 'ac',
    walking_cycling_distance_km: 50,
    vehicles: [
      {
        id: 1,
        type: 'motorcycle',
        fuel_type: 'petrol',
        monthly_distance_km: 300,
        name: 'My Motorcycle'
      },
      {
        id: 2,
        type: 'bicycle',
        fuel_type: 'human_power',
        monthly_distance_km: 50,
        name: 'My Bicycle'
      }
    ],
    public_transport_usage: 'weekly',
    climate_zone: 'tropical',
    meat_consumption: 'vegetarian',
    air_travel_hours: 10,
    cooking_method: 'gas',
    monthly_grocery_bill: 15000,
    waste_per_person: 2,
    recycling_practice: 'yes',
    income_level: 'low',
    location_type: 'urban',
    // Hidden fields with default values
    heating_efficiency: 0.8,
    cooling_efficiency: 0.7,
    heating_days: 30,
    cooling_days: 120,
    fuel_efficiency: 15,
    fuel_usage_liters: 30,
    recycling_rate: 0.6,
    composting_rate: 0.2,
    waste_bag_weekly_count: 2,
    tv_pc_daily_hours: 3,
    internet_daily_hours: 4,
    new_clothes_monthly: 1,
    renewable_energy_percentage: 0.05,
    home_efficiency: 0.75,
    climate_impact_factor: 1.2,
    lifestyle_impact_score: 0.6,
    waste_bag_size: 0,
    shopping_frequency: 'weekly',
    social_activity: 'medium',
    public_transport_availability: 'good',
    body_type: 'average',
    sex: 'male'
  }), []);

  const [formData, setFormData] = useState(defaultFormData);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [progress, setProgress] = useState(0);
  const [currentStep, setCurrentStep] = useState(0);

  useEffect(() => {
    setProgress(calculateProgress(formData));
  }, [formData]);

  // Function to reset form to empty values
  const resetForm = useCallback(() => {
    const emptyFormData = {
      household_size: '',
      home_size_sqft: '',
      home_type: '',
      home_age: '',
      electricity_usage_kwh: '',
      heating_energy_source: '',
      cooling_energy_source: '',
      walking_cycling_distance_km: '',
      vehicles: [],
      public_transport_usage: '',
      climate_zone: '',
      meat_consumption: '',
      air_travel_hours: '',
      cooking_method: '',
      monthly_grocery_bill: '',
      waste_per_person: '',
      recycling_practice: '',
      income_level: '',
      location_type: '',
      // Hidden fields with empty values
      heating_efficiency: '',
      cooling_efficiency: '',
      heating_days: '',
      cooling_days: '',
      fuel_efficiency: '',
      fuel_usage_liters: '',
      recycling_rate: '',
      composting_rate: '',
      waste_bag_weekly_count: '',
      tv_pc_daily_hours: '',
      internet_daily_hours: '',
      new_clothes_monthly: '',
      renewable_energy_percentage: '',
      home_efficiency: '',
      climate_impact_factor: '',
      lifestyle_impact_score: '',
      waste_bag_size: '',
      shopping_frequency: '',
      social_activity: '',
      public_transport_availability: '',
      body_type: '',
      sex: ''
    };
    setFormData(emptyFormData);
    setCurrentStep(0);
  }, []);

  // Reset form when component mounts (fresh start)
  useEffect(() => {
    resetForm();
  }, [resetForm]);

  // Step navigation functions
  const nextStep = () => {
    if (currentStep < formSections.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const goToStep = (step) => {
    setCurrentStep(step);
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm(formData)) {
      alert('Please fill in all required fields');
      return;
    }

    setIsSubmitting(true);
    
    try {
      const result = await submitCarbonData(formData);
      
      // Save results to localStorage
      localStorage.setItem('carbonFootprintResults', JSON.stringify(result));
      
      // Reset form after successful submission
      resetForm();
      
      // Navigate to results page
      navigate('/results');
    } catch (error) {
      console.error('Error calculating carbon footprint:', error);
      alert('Error calculating carbon footprint: ' + error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  const formSections = [
    {
      id: 'household',
      title: '🏠 Household Information',
      icon: '🏠',
      fields: [
        { name: 'household_size', label: 'Household Size', type: 'slider', min: 1, max: 15, value: formData.household_size, unit: 'people' },
        { name: 'home_size_sqft', label: 'Home Size (sq ft)', type: 'slider', min: 200, max: 5000, value: formData.home_size_sqft, unit: 'sq ft' },
        { name: 'home_type', label: 'Home Type', type: 'select', options: [
          { value: 'apartment', label: 'Apartment' },
          { value: 'independent_house', label: 'Independent House' },
          { value: 'villa', label: 'Villa' }
        ], value: formData.home_type },
        { name: 'home_age', label: 'Home Age (years)', type: 'number', min: 0, max: 50, value: formData.home_age }
      ]
    },
    {
      id: 'energy',
      title: '⚡ Energy Usage',
      icon: '⚡',
      fields: [
        { name: 'electricity_usage_kwh', label: 'Monthly Electricity Usage (kWh)', type: 'slider', min: 50, max: 2000, value: formData.electricity_usage_kwh, unit: 'kWh' },
        { name: 'heating_energy_source', label: 'Heating Energy Source', type: 'select', options: [
          { value: 'electric', label: 'Electric' },
          { value: 'gas', label: 'Gas' },
          { value: 'solar', label: 'Solar' },
          { value: 'none', label: 'None' }
        ], value: formData.heating_energy_source },
        { name: 'cooling_energy_source', label: 'Cooling Energy Source', type: 'select', options: [
          { value: 'ac', label: 'AC' },
          { value: 'fan', label: 'Fan' },
          { value: 'cooler', label: 'Cooler' },
          { value: 'none', label: 'None' }
        ], value: formData.cooling_energy_source }
      ]
    },
    {
      id: 'transportation',
      title: '🚗 Transportation',
      icon: '🚗',
      fields: [
        { name: 'walking_cycling_distance_km', label: 'Monthly Walking/Cycling Distance (km)', type: 'slider', min: 0, max: 500, value: formData.walking_cycling_distance_km, unit: 'km' },
        { name: 'public_transport_usage', label: 'Public Transport Usage', type: 'select', options: [
          { value: 'daily', label: 'Daily' },
          { value: 'weekly', label: 'Weekly' },
          { value: 'monthly', label: 'Monthly' },
          { value: 'rarely', label: 'Rarely' },
          { value: 'never', label: 'Never' }
        ], value: formData.public_transport_usage }
      ],
      customComponent: 'VehicleManager'
    },
    {
      id: 'climate',
      title: '🌍 Climate & Lifestyle',
      icon: '🌍',
      fields: [
        { name: 'climate_zone', label: 'Climate Zone', type: 'select', options: [
          { value: 'tropical', label: 'Tropical' },
          { value: 'temperate', label: 'Temperate' },
          { value: 'arid', label: 'Arid' },
          { value: 'coastal', label: 'Coastal' }
        ], value: formData.climate_zone },
        { name: 'meat_consumption', label: 'Meat Consumption', type: 'select', options: [
          { value: 'vegetarian', label: 'Vegetarian' },
          { value: 'occasional', label: 'Occasional' },
          { value: 'regular', label: 'Regular' }
        ], value: formData.meat_consumption },
        { name: 'air_travel_hours', label: 'Air Travel Hours per Year', type: 'number', min: 0, max: 100, value: formData.air_travel_hours },
        { name: 'cooking_method', label: 'Cooking Method', type: 'select', options: [
          { value: 'gas', label: 'Gas' },
          { value: 'electric', label: 'Electric' },
          { value: 'induction', label: 'Induction' },
          { value: 'traditional', label: 'Traditional' }
        ], value: formData.cooking_method }
      ]
    },
    {
      id: 'waste',
      title: '♻️ Waste & Consumption',
      icon: '♻️',
      fields: [
        { name: 'monthly_grocery_bill', label: 'Monthly Grocery Bill (₹)', type: 'number', min: 2000, max: 50000, value: formData.monthly_grocery_bill },
        { name: 'waste_per_person', label: 'Waste per Person (kg/week)', type: 'number', min: 1, max: 5, step: 0.1, value: formData.waste_per_person },
        { name: 'recycling_practice', label: 'Recycling Practice', type: 'select', options: [
          { value: 'yes', label: 'Yes' },
          { value: 'no', label: 'No' },
          { value: 'sometimes', label: 'Sometimes' }
        ], value: formData.recycling_practice }
      ]
    },
    {
      id: 'personal',
      title: '👥 Personal Information',
      icon: '👥',
      fields: [
        { name: 'income_level', label: 'Income Level', type: 'select', options: [
          { value: 'low', label: 'Low' },
          { value: 'middle', label: 'Middle' },
          { value: 'high', label: 'High' }
        ], value: formData.income_level },
        { name: 'location_type', label: 'Location Type', type: 'select', options: [
          { value: 'urban', label: 'Urban' },
          { value: 'semi_urban', label: 'Semi-urban' },
          { value: 'rural', label: 'Rural' }
        ], value: formData.location_type }
      ]
    }
  ];

  return (
    <Container>
      <ProgressBar progress={progress} />
      
      <Header>
        <Title>🌱 Carbon Footprint Calculator</Title>
        <Subtitle>
          Discover your environmental impact with our interactive calculator and get personalized recommendations to reduce your carbon footprint.
        </Subtitle>
      </Header>

      <InfoSection>
        <InfoContent>
          <InfoIcon>📋</InfoIcon>
          <InfoText>
            <h3>Important Information</h3>
            <p>After submitting the form, you'll be redirected to a detailed results page with your carbon footprint analysis and personalized recommendations!</p>
          </InfoText>
        </InfoContent>
        <InfoLink>
          <a href="/results">🔗 View Previous Results</a>
        </InfoLink>
      </InfoSection>

      <form onSubmit={handleSubmit}>
        {/* Step Indicator */}
        <StepNavigation>
          <div></div>
          
          <StepIndicator>
            Step {currentStep + 1} of {formSections.length}
            <StepDots>
              {formSections.map((_, index) => (
                <StepDot
                  key={index}
                  active={index === currentStep}
                  onClick={() => goToStep(index)}
                />
              ))}
            </StepDots>
          </StepIndicator>
          
          <StepButton 
            type="button" 
            className="reset-button" 
            onClick={resetForm}
          >
            🔄 Reset
          </StepButton>
        </StepNavigation>

        {/* Current Step Form Section */}
        <FormGrid>
          <FormSection
            key={formSections[currentStep].id}
            title={formSections[currentStep].title}
            icon={formSections[currentStep].icon}
            fields={formSections[currentStep].fields}
            formData={formData}
            onInputChange={handleInputChange}
            animationDelay={0}
            customComponent={formSections[currentStep].customComponent}
          />
        </FormGrid>

        {/* Step Navigation Buttons */}
        <StepNavigation>
          <StepButton 
            type="button" 
            className="prev-button" 
            onClick={prevStep}
            disabled={currentStep === 0}
          >
            ← Previous
          </StepButton>
          
          {currentStep === formSections.length - 1 ? (
            <SubmitButton>
              <button type="submit" disabled={isSubmitting}>
                {isSubmitting ? 'Calculating...' : 'Calculate Carbon Footprint'}
              </button>
            </SubmitButton>
          ) : (
            <StepButton 
              type="button" 
              onClick={nextStep}
            >
              Next →
            </StepButton>
          )}
        </StepNavigation>
      </form>
    </Container>
  );
};

export default CarbonCalculator;
