import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import styled from 'styled-components';
import CarbonCalculator from './components/CarbonCalculator';
import Results from './components/Results';
import FloatingElements from './components/FloatingElements';
import { initScrollAnimations } from './utils/scrollAnimations';

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
`;

function App() {
  useEffect(() => {
    // Initialize scroll animations when app loads
    initScrollAnimations();
  }, []);

  return (
    <AppContainer className="scroll-container">
      {/* Carbon Footprint Issues & Solutions Video Background */}
      <video 
        className="nature-video-background"
        autoPlay 
        muted 
        loop 
        playsInline
        poster="https://images.unsplash.com/photo-1569163139394-de446b5a4b0a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80"
      >
        <source src="https://player.vimeo.com/external/371433846.hd.mp4?s=236da2f3c0fd273d2c6d9a064f3ae35579b2bbdf&profile_id=175&oauth2_token_id=57447761" type="video/mp4" />
        <source src="https://player.vimeo.com/external/434819439.hd.mp4?s=4a5b5b5b5b5b5b5b5b5b5b5b5b5b5b5b5b5b5b5b&profile_id=175&oauth2_token_id=57447761" type="video/mp4" />
        <source src="https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerBlazes.mp4" type="video/mp4" />
        <source src="https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ForBiggerFun.mp4" type="video/mp4" />
        {/* Fallback image if video doesn't load */}
        <img 
          src="https://images.unsplash.com/photo-1569163139394-de446b5a4b0a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80" 
          alt="Carbon footprint issues and solutions background" 
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        />
      </video>
      
      {/* Overlay for better text readability */}
      <div className="nature-video-overlay"></div>
      
      <FloatingElements />
      <Router>
        <Routes>
          <Route path="/" element={<CarbonCalculator />} />
          <Route path="/results" element={<Results />} />
        </Routes>
      </Router>
    </AppContainer>
  );
}

export default App;
