import React from 'react';
import styled, { keyframes } from 'styled-components';
import FormField from './FormField';

const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const SectionContainer = styled.div`
  background: rgba(255, 255, 255, 0.98);
  padding: 35px;
  margin: 0;
  border-radius: 24px;
  box-shadow: 0 8px 32px rgba(0,0,0,0.1);
  position: relative;
  overflow: visible;
  backdrop-filter: blur(15px);
  border: 2px solid rgba(255, 255, 255, 0.4);
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  height: 100%;
  min-height: 450px;
  display: flex;
  flex-direction: column;
  align-self: stretch;
  animation: ${fadeInUp} 0.8s ease-out;
  animation-delay: ${props => props.animationDelay}s;
  animation-fill-mode: both;
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #4CAF50, #45a049, #2E8B57);
    border-radius: 24px 24px 0 0;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 50px rgba(0,0,0,0.15);
    background: rgba(255, 255, 255, 1);
    border-color: rgba(76, 175, 80, 0.3);
    
    &::before {
      opacity: 1;
    }
  }

  @media (max-width: 1000px) {
    min-height: 400px;
    padding: 30px;
  }

  @media (max-width: 768px) {
    min-height: 350px;
    padding: 25px;
    border-radius: 20px;
  }
`;

const SectionHeader = styled.h2`
  color: #333;
  margin-bottom: 20px;
  font-size: 1.2em;
  display: flex;
  align-items: center;
  gap: 8px;
  padding-top: 0;
  font-weight: 600;
  border-bottom: 2px solid #f0f0f0;
  padding-bottom: 10px;

  &::before {
    content: '';
    width: 4px;
    height: 24px;
    background: linear-gradient(135deg, #4CAF50, #45a049);
    border-radius: 3px;
    margin-right: 5px;
  }

  @media (max-width: 768px) {
    font-size: 1.1em;
    margin-bottom: 15px;
  }
`;

const FieldsContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 20px;

  @media (max-width: 768px) {
    gap: 15px;
  }
`;

const FormSection = ({ title, icon, fields, formData, onInputChange, animationDelay }) => {
  return (
    <SectionContainer animationDelay={animationDelay}>
      <SectionHeader>
        {icon} {title}
      </SectionHeader>
      <FieldsContainer>
        {fields.map((field) => (
          <FormField
            key={field.name}
            field={field}
            value={formData[field.name]}
            onChange={(value) => onInputChange(field.name, value)}
          />
        ))}
      </FieldsContainer>
    </SectionContainer>
  );
};

export default FormSection;
