import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    console.log('Making API request:', config.method?.toUpperCase(), config.url);
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    console.log('API response received:', response.status, response.data);
    return response;
  },
  (error) => {
    console.error('API error:', error.response?.status, error.response?.data);
    return Promise.reject(error);
  }
);

export const submitCarbonData = async (formData) => {
  try {
    console.log('Submitting carbon data:', formData);
    
    // Transform vehicles array into individual fields expected by backend
    const transformedData = transformFormDataForBackend(formData);
    console.log('Transformed data for backend:', transformedData);
    
    const response = await api.post('/predict', transformedData);
    
    if (!response.data) {
      throw new Error('No data received from server');
    }

    // Calculate breakdown for the results
    const breakdown = calculateBreakdown(formData, response.data.predicted_emissions);
    
    const results = {
      predicted_emissions: response.data.predicted_emissions,
      confidence_score: response.data.confidence_score,
      recommendations: response.data.recommendations || [],
      breakdown: breakdown,
      input_data: formData,
      timestamp: new Date().toISOString()
    };

    console.log('Results prepared:', results);
    return results;
  } catch (error) {
    console.error('Error submitting carbon data:', error);
    
    if (error.response) {
      // Server responded with error status
      throw new Error(`Server Error: ${error.response.status} - ${error.response.data?.message || error.response.statusText}`);
    } else if (error.request) {
      // Request was made but no response received
      throw new Error('Network Error: Unable to connect to server. Please check your internet connection.');
    } else {
      // Something else happened
      throw new Error(`Request Error: ${error.message}`);
    }
  }
};

export const fetchModelDetails = async () => {
  try {
    const response = await api.get('/model-details');
    return response.data;
  } catch (error) {
    console.warn('Could not fetch model details:', error);
    return {
      model_type: 'Random Forest',
      version: '1.0.0',
      training_date: '2024-01-01'
    };
  }
};

// Transform form data to match backend API expectations
const transformFormDataForBackend = (formData) => {
  // Calculate total vehicle distance from vehicles array
  let totalVehicleDistance = 0;
  let vehicleType = 'none';
  let vehiclesPerHousehold = 0;
  
  if (formData.vehicles && formData.vehicles.length > 0) {
    totalVehicleDistance = formData.vehicles.reduce((sum, vehicle) => 
      sum + (vehicle.monthly_distance_km || 0), 0
    );
    vehiclesPerHousehold = formData.vehicles.length;
    
    // Use the first vehicle's type and fuel for primary vehicle fields
    const primaryVehicle = formData.vehicles[0];
    vehicleType = primaryVehicle.type || 'none';
    fuelType = primaryVehicle.fuel_type || 'none';
  }

  // Map meat consumption to string value expected by main.py API
  const meatConsumptionMap = {
    'vegetarian': 'None',
    'occasional': 'Low',
    'regular': 'High'
  };

  // Map shopping frequency to string value expected by main.py API
  const shoppingFrequencyMap = {
    'daily': 'Daily',
    'weekly': 'Weekly',
    'monthly': 'Monthly',
    'rarely': 'Rarely'
  };

  // Map social activity to string value expected by main.py API
  const socialActivityMap = {
    'low': 'Low',
    'medium': 'Medium',
    'high': 'High'
  };

  // Map heating energy source to expected format
  const heatingEnergyMap = {
    'electric': 'Electric',
    'gas': 'Natural Gas',
    'solar': 'Solar',
    'none': 'None'
  };

  // Map vehicle type to expected format
  const vehicleTypeMap = {
    'bicycle': 'Bicycle',
    'motorcycle': 'Motorcycle',
    'scooter': 'Scooter',
    'car_small': 'Gasoline',
    'car_medium': 'Gasoline',
    'car_large': 'Gasoline',
    'auto_rickshaw': 'Gasoline',
    'bus': 'Gasoline',
    'train': 'Electric',
    'metro': 'Electric',
    'taxi': 'Gasoline',
    'walking': 'None',
    'none': 'None'
  };

  // Map climate zone to expected format
  const climateZoneMap = {
    'tropical': 'Hot',
    'temperate': 'Moderate',
    'arid': 'Hot',
    'coastal': 'Moderate'
  };

  return {
    household_size: formData.household_size || 1,
    electricity_usage_kwh: formData.electricity_usage_kwh || 0,
    home_size_sqft: formData.home_size_sqft || 0,
    heating_energy_source: heatingEnergyMap[formData.heating_energy_source] || 'Electric',
