    vehicles_per_household: vehiclesPerHousehold,
    monthly_grocery_bill: formData.monthly_grocery_bill || 300.0
  };
};

// Helper function to calculate breakdown (moved from utils to avoid circular dependency)
const calculateBreakdown = (inputs, totalFootprint) => {
  const breakdown = {
    electricity: 0,
    transportation: 0,
    heating: 0,
    waste: 0,
    lifestyle: 0
  };

