import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import FormSection from './FormSection';
import ProgressBar from './ProgressBar';
import { calculateProgress, validateForm } from '../utils/formUtils';
import { submitCarbonData } from '../services/api';

const Container = styled.div`
  max-width: 1600px;
  margin: 0 auto;
  padding: 30px;
  background: rgba(255, 255, 255, 0.98);
  min-height: 100vh;
  backdrop-filter: blur(20px);
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.15);
  border-radius: 30px;
  margin: 20px;
  overflow-y: auto;
  border: 2px solid rgba(255, 255, 255, 0.3);
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #4CAF50, #45a049, #2E8B57);
    border-radius: 30px 30px 0 0;
  }
  
  @media (max-width: 768px) {
    margin: 10px;
    padding: 20px;
    border-radius: 20px;
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 40px;
  padding: 30px 0;
  background: linear-gradient(135deg, rgba(76, 175, 80, 0.05), rgba(69, 160, 73, 0.05));
  border-radius: 20px;
  border: 1px solid rgba(76, 175, 80, 0.1);
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(76, 175, 80, 0.03) 0%, transparent 70%);
    animation: rotate 20s linear infinite;
  }
  
  @keyframes rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }
`;

const Title = styled.h1`
  font-size: 3em;
  margin-bottom: 20px;
  background: linear-gradient(135deg, #2E8B57, #3CB371, #4CAF50);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
