import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import styled from 'styled-components';
import CarbonCalculator from './components/CarbonCalculator';
import Results from './components/Results';
import FloatingElements from './components/FloatingElements';
import { initScrollAnimations } from './utils/scrollAnimations';

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
`;

function App() {
  useEffect(() => {
    // Initialize scroll animations when app loads
    initScrollAnimations();
  }, []);

  return (
    <AppContainer className="scroll-container">
      {/* Climate Change Video Background - YouTube Embed - Full Screen */}
      <iframe 
        className="nature-video-background"
        src="https://www.youtube.com/embed/hyRFw0uxyGU?autoplay=1&mute=1&loop=1&playlist=hyRFw0uxyGU&controls=0&showinfo=0&rel=0&modestbranding=1&iv_load_policy=3&fs=1&disablekb=1&start=0&end=0"
        title="Climate Change - A fight we picked"
        frameBorder="0"
        allow="autoplay; encrypted-media; fullscreen"
        allowFullScreen
        style={{
          position: 'fixed',
          top: '50%',
          left: '50%',
          width: '100vw',
          height: '100vh',
          minWidth: '100vw',
          minHeight: '100vh',
          zIndex: -3,
          objectFit: 'cover',
          opacity: 0.8,
          filter: 'brightness(1.2) saturate(1.6) contrast(1.3) hue-rotate(15deg)',
          transform: 'translate(-50%, -50%) scale(1.1)',
          pointerEvents: 'none',
          border: 'none',
          outline: 'none'
        }}
      />
      
      {/* Fallback image if video doesn't load */}
      <img 
        src="https://images.unsplash.com/photo-1611273426858-450d8e3c9fce?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80" 
        alt="Climate change background - Nature fighting for survival" 
        style={{ 
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%', 
          height: '100%', 
          objectFit: 'cover',
          zIndex: -4,
          display: 'none' // Hidden by default, shown if iframe fails
        }}
      />
      
      {/* Overlay for better text readability */}
      <div className="nature-video-overlay"></div>
      
      <FloatingElements />
      <Router>
        <Routes>
          <Route path="/" element={<CarbonCalculator />} />
          <Route path="/results" element={<Results />} />
        </Routes>
      </Router>
    </AppContainer>
  );
}

export default App;
