import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { Doughnut, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: rgba(255, 255, 255, 0.98);
  min-height: 100vh;
  backdrop-filter: blur(15px);
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1);
  border-radius: 25px;
  margin: 15px;
  overflow-y: auto;
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const Header = styled.div`
  background: linear-gradient(135deg, #FF6B6B, #4ECDC4, #45B7D1);
  color: white;
  padding: 50px;
  border-radius: 25px;
  margin-bottom: 30px;
  text-align: center;
  box-shadow: 0 25px 50px rgba(0,0,0,0.2);
  position: relative;
  overflow: hidden;
  border: 3px solid rgba(255,255,255,0.3);
`;

const HeaderTitle = styled.h1`
  font-size: 3em;
  margin-bottom: 10px;
  position: relative;
  z-index: 1;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const Subtitle = styled.p`
  font-size: 1.2em;
  opacity: 0.9;
  position: relative;
  z-index: 1;
  margin-bottom: 30px;
`;

const StatsContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 40px;
  margin-top: 30px;
  position: relative;
  z-index: 1;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const StatItem = styled.div`
  text-align: center;
  background: rgba(255,255,255,0.2);
  padding: 20px;
  border-radius: 15px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255,255,255,0.3);
  transition: all 0.3s ease;
  min-width: 120px;

  &:hover {
    transform: translateY(-5px);
    background: rgba(255,255,255,0.3);
  }

  i {
    font-size: 2em;
    margin-bottom: 10px;
    display: block;
  }

  span {
    font-size: 1.8em;
    font-weight: bold;
    display: block;
    margin-bottom: 5px;
  }

  label {
    font-size: 0.9em;
    opacity: 0.8;
    text-transform: uppercase;
    letter-spacing: 1px;
  }
`;

const ResultCard = styled.div`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  padding: 40px;
  margin: 30px 0;
  border-radius: 25px;
  box-shadow: 0 20px 40px rgba(0,0,0,0.1);
  border: 1px solid rgba(255,255,255,0.2);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #FF6B6B, #4ECDC4, #45B7D1, #96CEB4);
  }

  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 30px 60px rgba(0,0,0,0.2);
  }
`;

const MainResult = styled.div`
  text-align: center;
  margin-bottom: 40px;
  position: relative;
`;

const FootprintValue = styled.div`
  font-size: 4em;
  font-weight: 800;
  background: linear-gradient(135deg, #2E8B57, #3CB371);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin: 30px 0;
  text-shadow: 0 0 30px rgba(46, 139, 87, 0.3);
  animation: pulse 2s ease-in-out infinite;

  @keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
  }

  @media (max-width: 768px) {
    font-size: 2.5em;
  }
`;

const Badges = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 15px;
  margin: 30px 0;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Badge = styled.div`
  background: linear-gradient(135deg, #E8F5E8, #C8E6C9);
  color: #2E8B57;
  padding: 15px 30px;
  border-radius: 50px;
  font-weight: 600;
  font-size: 1.1em;
  box-shadow: 0 8px 16px rgba(46, 139, 87, 0.2);
  border: 2px solid rgba(46, 139, 87, 0.1);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 24px rgba(46, 139, 87, 0.3);
  }
`;

const ChartsContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 30px;
  margin-top: 20px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const ChartWrapper = styled.div`
  background: white;
  padding: 20px;
  border-radius: 15px;
  box-shadow: 0 8px 16px rgba(0,0,0,0.1);
  text-align: center;
`;

const ComparisonGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const ComparisonItem = styled.div`
  background: white;
  padding: 25px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 0 8px 16px rgba(0,0,0,0.1);
  transition: all 0.3s ease;
  border: 2px solid transparent;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0,0,0,0.15);
    border-color: #4ECDC4;
  }

  .icon {
    font-size: 2.5em;
    margin-bottom: 15px;
  }

  .label {
    font-weight: bold;
    color: #666;
    margin-bottom: 10px;
    font-size: 0.9em;
  }

  .value {
    font-size: 1.2em;
    font-weight: bold;
    color: #333;
  }
`;

const Recommendations = styled.div`
  margin-top: 40px;
`;

const Recommendation = styled.div`
  background: white;
  border-radius: 15px;
  padding: 25px;
  margin: 20px 0;
  box-shadow: 0 8px 16px rgba(0,0,0,0.1);
  transition: all 0.3s ease;
  border-left: 6px solid #2E8B57;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 4px;
    background: linear-gradient(90deg, #2E8B57, #3CB371);
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0,0,0,0.15);
  }

  h4 {
    color: #2E8B57;
    margin-bottom: 15px;
    font-size: 1.3em;
    display: flex;
    align-items: center;
    gap: 10px;
  }

  p {
    margin: 10px 0;
    line-height: 1.6;
  }

  strong {
    color: #333;
  }
`;

const ActionButtons = styled.div`
  text-align: center;
  margin: 50px 0;
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 20px;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

// Navigation Styles
const NavigationContainer = styled.div`
  position: sticky;
  top: 20px;
  z-index: 100;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(15px);
  border-radius: 20px;
  padding: 20px;
  margin-bottom: 30px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const NavigationTitle = styled.h3`
  text-align: center;
  margin-bottom: 20px;
  color: #2c3e50;
  font-size: 1.2em;
  font-weight: 600;
`;

const NavigationButtons = styled.div`
  display: flex;
  gap: 15px;
  justify-content: center;
  flex-wrap: wrap;
`;

const NavButton = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 12px 20px;
  border-radius: 25px;
  font-size: 0.9em;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.3);
  display: flex;
  align-items: center;
  gap: 8px;
  min-width: 140px;
  justify-content: center;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(102, 126, 234, 0.4);
    background: linear-gradient(135deg, #5a6fd8 0%, #6a4190 100%);
  }

  &:active {
    transform: translateY(-1px);
  }

  &.active {
    background: linear-gradient(135deg, #FF6B6B 0%, #4ECDC4 100%);
    box-shadow: 0 6px 20px rgba(255, 107, 107, 0.4);
  }

  i {
    font-size: 1.1em;
  }

  @media (max-width: 768px) {
    min-width: 120px;
    padding: 10px 16px;
    font-size: 0.85em;
  }
`;

const Button = styled.button`
  background: linear-gradient(135deg, #2E8B57, #3CB371);
  color: white;
  padding: 15px 30px;
  border: none;
  border-radius: 50px;
  cursor: pointer;
  font-size: 16px;
  font-weight: 600;
  text-decoration: none;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  transition: all 0.3s ease;
  box-shadow: 0 8px 16px rgba(46, 139, 87, 0.3);

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 24px rgba(46, 139, 87, 0.4);
    background: linear-gradient(135deg, #228B22, #2E8B57);
  }

  &.secondary {
    background: linear-gradient(135deg, #6C757D, #868E96);
    box-shadow: 0 8px 16px rgba(108, 117, 125, 0.3);

    &:hover {
      background: linear-gradient(135deg, #5A6268, #6C757D);
      box-shadow: 0 12px 24px rgba(108, 117, 125, 0.4);
    }
  }
`;

const Loading = styled.div`
  text-align: center;
  padding: 80px;
  font-size: 20px;
  color: white;
  background: rgba(255,255,255,0.1);
  border-radius: 20px;
  backdrop-filter: blur(10px);
`;

const Error = styled.div`
  background: linear-gradient(135deg, #F8D7DA, #F5C6CB);
  color: #721C24;
  padding: 30px;
  border-radius: 15px;
  margin: 30px 0;
  border-left: 6px solid #DC3545;
  box-shadow: 0 8px 16px rgba(220, 53, 69, 0.2);

  h3 {
    margin-bottom: 15px;
    font-size: 1.3em;
  }
`;

const Results = () => {
  const navigate = useNavigate();
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeSection, setActiveSection] = useState('overview');
  const [showContent, setShowContent] = useState(false);
  
  // Refs for each section
  const overviewRef = useRef(null);
  const mainResultRef = useRef(null);
  const chartsRef = useRef(null);
  const comparisonRef = useRef(null);
  const recommendationsRef = useRef(null);

  useEffect(() => {
    loadResults();
  }, []);

  // Scroll detection to update active section
  useEffect(() => {
    const handleScroll = () => {
      const sections = [
        { id: 'overview', ref: overviewRef },
        { id: 'mainResult', ref: mainResultRef },
        { id: 'charts', ref: chartsRef },
        { id: 'comparison', ref: comparisonRef },
        { id: 'recommendations', ref: recommendationsRef }
      ];

      const scrollPosition = window.scrollY + 100;

      for (let i = sections.length - 1; i >= 0; i--) {
        const section = sections[i];
        if (section.ref.current) {
          const sectionTop = section.ref.current.offsetTop;
          if (scrollPosition >= sectionTop) {
            setActiveSection(section.id);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Navigation function
  const scrollToSection = (sectionId) => {
    setActiveSection(sectionId);
    const refs = {
      overview: overviewRef,
      mainResult: mainResultRef,
      charts: chartsRef,
      comparison: comparisonRef,
      recommendations: recommendationsRef
    };
    
    const targetRef = refs[sectionId];
    if (targetRef && targetRef.current) {
      targetRef.current.scrollIntoView({
        behavior: 'smooth',
        block: 'start'
      });
    }
  };

  const loadResults = () => {
    try {
      const savedResults = localStorage.getItem('carbonFootprintResults');
      if (savedResults) {
        const parsedResults = JSON.parse(savedResults);
        setResults(parsedResults);
      } else {
        setError('No results data available. Please go back and calculate your carbon footprint first.');
      }
    } catch (err) {
      setError('Error loading results data.');
    } finally {
      setLoading(false);
    }
  };

  const calculateCarbonScore = (footprint) => {
    if (footprint < 2000) return 'Excellent';
    if (footprint < 5000) return 'Good';
    if (footprint < 10000) return 'Average';
    if (footprint < 15000) return 'High';
    return 'Very High';
  };

  const calculateRank = (footprint) => {
    if (footprint < 2000) return 'Top 5%';
    if (footprint < 5000) return 'Top 25%';
    if (footprint < 10000) return 'Top 50%';
    if (footprint < 15000) return 'Top 75%';
    return 'Top 90%';
  };

  const createBreakdownChart = (breakdown) => {
    const defaultBreakdown = {
      'Electricity': 35,
      'Transportation': 25,
      'Heating': 20,
      'Waste': 15,
      'Lifestyle': 5
    };

    const data = breakdown && Object.keys(breakdown).length > 0 ? breakdown : defaultBreakdown;

    return {
      labels: Object.keys(data),
      datasets: [{
        data: Object.values(data),
        backgroundColor: [
          '#FF6B6B',
          '#4ECDC4',
          '#45B7D1',
          '#96CEB4',
          '#FFEAA7'
        ],
        borderWidth: 3,
        borderColor: '#fff'
      }]
    };
  };

  const createComparisonChart = (footprint) => {
    return {
      labels: ['Your Household', 'National Average', 'Global Average', 'Climate Target'],
      datasets: [{
        label: 'Carbon Footprint (kg CO2/year)',
        data: [footprint, 8500, 4800, 2000],
        backgroundColor: [
          '#FF6B6B',
          '#4ECDC4',
          '#45B7D1',
          '#96CEB4'
        ],
        borderWidth: 2,
        borderColor: '#fff'
      }]
    };
  };

  if (loading) {
    return (
      <Container>
        <Loading>
          <p> Loading your results...</p>
        </Loading>
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Error>
          <h3> Error Loading Results</h3>
          <p>{error}</p>
        </Error>
      </Container>
    );
  }

  if (!results) {
    return (
      <Container>
        <Error>
          <h3> No Results Found</h3>
          <p>Please go back and calculate your carbon footprint first.</p>
        </Error>
      </Container>
    );
  }

  const footprint = Math.round(results.predicted_emissions);
  const breakdown = results.breakdown || {};

  return (
    <Container>
      <Header ref={overviewRef}>
        <HeaderTitle> Carbon Footprint Dashboard</HeaderTitle>
        <Subtitle>Your personalized environmental impact analysis</Subtitle>
        <StatsContainer>
          <StatItem>
            <i className="fas fa-leaf"></i>
            <span>{calculateCarbonScore(footprint)}</span>
            <label>Carbon Score</label>
          </StatItem>
          <StatItem>
            <i className="fas fa-trophy"></i>
            <span>{calculateRank(footprint)}</span>
            <label>Rank</label>
          </StatItem>
          <StatItem>
            <i className="fas fa-chart-line"></i>
            <span> Stable</span>
            <label>Trend</label>
          </StatItem>
        </StatsContainer>
      </Header>

      <NavigationContainer>
        <NavigationTitle> Quick Navigation</NavigationTitle>
        <NavigationButtons>
          <NavButton 
            className={activeSection === 'overview' ? 'active' : ''}
            onClick={() => scrollToSection('overview')}
          >
            <i className="fas fa-home"></i>
            Overview
          </NavButton>
          <NavButton 
            className={activeSection === 'mainResult' ? 'active' : ''}
            onClick={() => scrollToSection('mainResult')}
          >
            <i className="fas fa-leaf"></i>
            Your Result
          </NavButton>
          <NavButton 
            className={activeSection === 'charts' ? 'active' : ''}
            onClick={() => scrollToSection('charts')}
          >
            <i className="fas fa-chart-pie"></i>
            Charts
          </NavButton>
          <NavButton 
            className={activeSection === 'comparison' ? 'active' : ''}
            onClick={() => scrollToSection('comparison')}
          >
            <i className="fas fa-balance-scale"></i>
            Comparison
          </NavButton>
          {results.recommendations && results.recommendations.length > 0 && (
            <NavButton 
              className={activeSection === 'recommendations' ? 'active' : ''}
              onClick={() => scrollToSection('recommendations')}
            >
              <i className="fas fa-star"></i>
              Tips
            </NavButton>
          )}
        </NavigationButtons>
      </NavigationContainer>

      <ResultCard ref={mainResultRef}>
        <MainResult>
          <h2><i className="fas fa-leaf"></i> Your Carbon Footprint</h2>
          <FootprintValue>{footprint.toLocaleString()} kg CO2/year</FootprintValue>
          <Badges>
            <Badge>Confidence: {(results.confidence_score * 100).toFixed(1)}%</Badge>
            <Badge>Random Forest Model</Badge>
          </Badges>
        </MainResult>
      </ResultCard>

      <ResultCard ref={chartsRef}>
        <h3><i className="fas fa-chart-pie"></i> Visual Analysis</h3>
        <ChartsContainer>
          <ChartWrapper>
            <Doughnut
              data={createBreakdownChart(breakdown)}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    position: 'bottom',
                    labels: {
                      padding: 20,
                      usePointStyle: true
                    }
                  },
                  title: {
                    display: true,
                    text: 'Carbon Footprint Breakdown',
                    font: { size: 16, weight: 'bold' }
                  }
                },
                animation: {
                  animateRotate: true,
                  animateScale: true
                }
              }}
            />
          </ChartWrapper>
          <ChartWrapper>
            <Bar
              data={createComparisonChart(footprint)}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    display: false
                  },
                  title: {
                    display: true,
                    text: 'Comparison with Benchmarks',
                    font: { size: 16, weight: 'bold' }
                  }
                },
                scales: {
                  y: {
                    beginAtZero: true,
                    title: {
                      display: true,
                      text: 'kg CO2/year'
                    }
                  }
                },
                animation: {
                  duration: 2000,
                  easing: 'easeInOutQuart'
                }
              }}
            />
          </ChartWrapper>
        </ChartsContainer>
      </ResultCard>

      <ResultCard ref={comparisonRef}>
        <h3><i className="fas fa-balance-scale"></i> National Context Comparison</h3>
        <ComparisonGrid>
          <ComparisonItem>
            <div className="icon"></div>
            <div className="label">Your Household</div>
            <div className="value">{footprint.toLocaleString()} kg CO2/year</div>
          </ComparisonItem>
          <ComparisonItem>
            <div className="icon"></div>
            <div className="label">National Average</div>
            <div className="value">8,500 kg CO2/year</div>
          </ComparisonItem>
          <ComparisonItem>
            <div className="icon"></div>
            <div className="label">Global Average</div>
            <div className="value">4,800 kg CO2/year</div>
          </ComparisonItem>
          <ComparisonItem>
            <div className="icon"></div>
            <div className="label">Climate Target</div>
            <div className="value">2,000 kg CO2/year</div>
          </ComparisonItem>
        </ComparisonGrid>
      </ResultCard>

      {results.recommendations && results.recommendations.length > 0 && (
        <ResultCard ref={recommendationsRef}>
          <Recommendations>
            <h3><i className="fas fa-star"></i> Personalized Recommendations</h3>
            {results.recommendations.map((rec, index) => (
              <Recommendation key={index}>
                <h4>{rec.category}</h4>
                <p><strong>Action:</strong> {rec.action}</p>
                <p><strong>Potential Savings:</strong> {rec.potential_savings}</p>
                <p><strong>Priority:</strong> {rec.priority}</p>
              </Recommendation>
            ))}
          </Recommendations>
        </ResultCard>
      )}

      <ActionButtons>
        <Button onClick={() => navigate('/')}>
          <i className="fas fa-calculator"></i> Calculate Another
        </Button>
        <Button className="secondary" onClick={() => window.print()}>
          <i className="fas fa-print"></i> Print Results
        </Button>
        <Button className="secondary" onClick={() => {
          const text = `My carbon footprint is ${footprint.toLocaleString()} kg CO2/year. Check out this carbon footprint calculator!`;
          if (navigator.share) {
            navigator.share({
              title: 'My Carbon Footprint Results',
              text: text,
              url: window.location.href
            });
          } else {
            navigator.clipboard.writeText(text).then(() => {
              alert('Results copied to clipboard!');
            });
          }
        }}>
          <i className="fas fa-share"></i> Share Results
        </Button>
      </ActionButtons>
    </Container>
  );
};

export default Results;
